mapper.select:
select
 bal.leave_id, bal.user_id, bal.leave_type, bal.leave_hours,bal.leave_hours_unit,bal.leave_dates,bal.leave_reason, bal.create_by, bal.create_time, bal.update_by,
 bal.update_time,bal.remark,bal.dept_id,bal.in_prj_flag,bal.leave_prj_name,bal.overtime_sur_Time,bal.annLea_sur_Time,bal.leave_begin_time,
 bal.leave_end_time,
 ifnull(wb.bill_status,2) as approval_Status
from busi_ask_leave bal
left join workflow_bill wb on bal.leave_id = wb.bill_id  and wb.workflow_id = 2
left join sys_user sys_user on sys_user.user_id = bal.user_id

     and bal.user_id = #{userId}
     and bal.leave_type = #{leaveType}
     and bal.create_time = #{createTime}
     and bal.leave_reason like  CONCAT('%',#{leaveReason},'%')
     and bal.update_time = #{updateTime}
     and bal.leave_begin_time >= #{beginTime}
     and bal.leave_end_time  <=  #{endTime} 
     and bal.create_by = #{createBy} 
    ${dataScope}

order by create_time desc

mapper.update:
update busi_ask_leave
user_id = #{userId}, leave_type = #{leaveType}, leave_hours = #{leaveHours}, leave_hours_unit = #{leaveHoursUnit}, leave_dates = #{leaveDates}, leave_reason = #{leaveReason}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, dept_id = #{deptId}, overtime_sur_Time = #{overtimeSurTime}, annLea_sur_Time = #{annLeaSurTime}, leave_begin_time = #{leaveBeginTime}, leave_end_time = #{leaveEndTime}, leave_prj_name = #{leavePrjName}, in_prj_flag = #{inPrjFlag},
where leave_id = #{leaveId}
