mapper.select:
select be.extra_work_id, be.user_id, be.extra_work_hours, be.extra_hours_unit, be.extra_work_dates, be.extra_work_Reason, be.dept_Id, be.extra_work_Prj_Name, be.in_prj_flag, be.overtime_sur_time, ifnull(wb.bill_status,2) as approval_Status, be.create_by, be.create_Time, be.update_by, be.update_Time, be.remark from busi_extra_work be left join workflow_bill wb on be.extra_work_id = wb.bill_id and wb.workflow_id = 1
and be.user_id = #{userId} and be.create_time = #{createTime}
order by be.create_time desc

mapper.update:
update busi_extra_work
user_id = #{userId}, extra_work_hours = #{extraWorkHours}, extra_hours_unit = #{extraHoursUnit}, extra_work_Reason = #{extraWorkReason}, dept_Id = #{deptId}, extra_work_Prj_Name = #{extraWorkPrjName}, in_prj_flag = #{inPrjFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, overtime_sur_time = #{overtimeSurTime},
where extra_work_id = #{extraWorkId}
