mapper.select:

from busi_task t

     and t.task_id = #{taskId}
     and t.task_number = #{taskNumber}
     and t.task_name = #{taskName}
     and t.project_id = #{projectId}
     and t.task_start_date = #{taskStartDate}
     and t.task_end_date = #{taskEndDate}
     and t.task_desc = #{taskDesc}
     and t.status = #{status}
     and t.task_progress = #{taskProgress}
     and t.time_progress = #{timeProgress}
     and t.task_status = #{taskStatus}
     and t.close_reason = #{closeReason}
     and t.sort_order = #{sortOrder}
     and t.create_by = #{createBy}
     and t.create_time = #{createTime}
     and t.update_by = #{updateBy}
     and t.update_time = #{updateTime}
     and t.remark = #{remark}

mapper.update:
update busi_task
task_number = #{taskNumber}, task_name = #{taskName}, project_id = #{projectId}, task_start_date = #{taskStartDate}, task_end_date = #{taskEndDate}, task_desc = #{taskDesc}, status = #{status}, task_progress = #{taskProgress}, time_progress = #{timeProgress}, task_status = #{taskStatus}, close_reason = #{closeReason}, sort_order = #{sortOrder}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where task_id = #{taskId}

mapper.select:
select t.task_id, t.task_number, t.task_name, t.project_id, t.task_start_date, t.task_end_date, t.task_desc, t.status, t.task_progress, t.task_status, t.close_reason, t.sort_order, t.create_by, t.create_time, t.update_by, t.update_time, t.remark
,(select count(1) from busi_task_member tm where tm.task_id = t.task_id) memberNums,
round(DATEDIFF(now(),task_start_date)/DATEDIFF(task_end_date,task_start_date) *100) time_progress,
ifnull((select max(tl.log_status) from busi_task_log tl where tl.task_id = t.task_id and tl.create_by = #{createBy} and curdate() = tl.create_date),0) baosongNum
from busi_task t

     and t.task_id = #{taskId}
     and t.task_number = #{taskNumber}
     and t.task_name = #{taskName}
     and t.project_id = #{projectId}
     and t.task_start_date >= #{taskStartDate}
     and t.task_end_date  <=  #{taskEndDate}
     and t.task_desc = #{taskDesc}
     and t.status = #{status}
     and t.task_progress = #{taskProgress}
     and t.time_progress = #{timeProgress}
     and t.task_status = #{taskStatus}
     and t.close_reason = #{closeReason}
     and t.sort_order = #{sortOrder}
     and t.remark = #{remark}

select count(1)
from busi_task_log tl
where tl.task_id = #{projectId}
