mapper.select:

from workflow_bill b

     and b.bill_id = #{billId}
     and b.workflow_id = #{workflowId}
     and b.workflow_name = #{workflowName}
     and b.workflow_group_id = #{workflowGroupId}
     and b.workflow_step_id = #{workflowStepId}
     and b.workflow_step_node_id = #{workflowStepNodeId}
     and b.workflow_node_id = #{workflowNodeId}
     and b.bill_status = #{billStatus}
     and b.orgin_handler = #{orginHandler}
     and b.sort_order = #{sortOrder}
     and b.create_by = #{createBy}
     and b.create_time = #{createTime}
     and b.update_by = #{updateBy}
     and b.update_time = #{updateTime}
     and b.remarks = #{remarks}

mapper.update:
update workflow_bill
workflow_id = #{workflowId}, workflow_name = #{workflowName}, workflow_group_id = #{workflowGroupId}, workflow_step_id = #{workflowStepId}, workflow_step_node_id = #{workflowStepNodeId}, workflow_node_id = #{workflowNodeId}, bill_status = #{billStatus}, orgin_handler = #{orginHandler}, sort_order = #{sortOrder}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remarks = #{remarks},
where bill_id = #{billId}

update workflow_bill
workflow_name = #{workflowName}, workflow_group_id = #{workflowGroupId}, workflow_step_id = #{workflowStepId}, workflow_step_node_id = #{workflowStepNodeId}, workflow_node_id = #{workflowNodeId}, bill_status = #{billStatus}, orgin_handler = #{orginHandler}, sort_order = #{sortOrder}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remarks = #{remarks},
where bill_id = #{billId} and workflow_id = #{workflowId}
