mapper.select:

from workflow_bill_trace bt

     and bt.check_id = #{checkId}
     and bt.bill_id = #{billId}
     and bt.workflow_id = #{workflowId}
     and bt.workflow_name = #{workflowName}
     and bt.workflow_group_id = #{workflowGroupId}
     and bt.workflow_step_id = #{workflowStepId}
     and bt.workflow_step_node_id = #{workflowStepNodeId}
     and bt.workflow_node_id = #{workflowNodeId}
     and bt.checker_user_id = #{checkerUserId}
     and bt.checker_user_name = #{checkerUserName}
     and bt.checker_dept_name = #{checkerDeptName}
     and bt.checker_user_post = #{checkerUserPost}
     and bt.check_status = #{checkStatus}
     and bt.check_remarks = #{checkRemarks}
     and bt.check_attachment_path = #{checkAttachmentPath}
     and bt.create_by = #{createBy}
     and bt.create_time = #{createTime}

mapper.update:
update workflow_bill_trace
bill_id = #{billId}, workflow_id = #{workflowId}, workflow_name = #{workflowName}, workflow_group_id = #{workflowGroupId}, workflow_step_id = #{workflowStepId}, workflow_step_node_id = #{workflowStepNodeId}, workflow_node_id = #{workflowNodeId}, checker_user_id = #{checkerUserId}, checker_user_name = #{checkerUserName}, checker_dept_name = #{checkerDeptName}, checker_user_post = #{checkerUserPost}, check_status = #{checkStatus}, check_remarks = #{checkRemarks}, check_attachment_path = #{checkAttachmentPath}, create_by = #{createBy}, create_time = #{createTime},
where check_id = #{checkId}
